sections.code:
body,
.ContentItem-title {
    font-family: "Hiragino Sans GB", "Source Han Sans SC", "Miscrosoft Yahei", sans-serif;
    font-size: 1.5rem !important;
}

.QuestionHeader-title,
.List-headerText {
    font-size: 1rem;
}

/* .PageHeader, */
.Question-sideColumn.Question-sideColumn--sticky {
    display: none;
}

.ContentItem-actions {
    padding: 0;
}

/* .AppHeader-inner {
    height: 100%;
} */
.Question-main {
    margin-left: auto !important;
    right: auto !important;
    width: 90%;
}
.QuestionHeader-content,
.Question-mainColumn {
    width: 100%;
}

body,
.ContentItem-title {
    font-family: "Hiragino Sans GB", "Source Han Sans SC", "Miscrosoft Yahei", sans-serif;
    font-size: 1.25rem;
}
code {
    font-family: "Source Code Pro", Monaco, Consolas, monospace !important;
}
.FeedSource-firstline {
    font-size: 1rem;
}

.Sticky.AppHeader.is-fixed {
    position: absolute;
}

.GlobalSideBar div div.Sticky.is-fixed {
    position: absolute;
}

.GlobalSideBar-navList,
.Footer {
    display: none;
}
.Topstory-container,
.Topstory-mainColumn {
    width: 90%
}

.GlobalWrite-nav {
    flex-wrap: wrap;
}

.GlobalWrite-navItem,
.GlobalSideBar-categoryItem {
    width: 100%;
}

.RichContent.is-collapsed .RichContent-inner {
    max-height: 100%;
}

@media screen and (min-width: 1000px) {
    .ztext .origin_image {
        max-width: 60% !important;
    }
}
@media screen and (min-width: 800px) {
    .ztext .origin_image {
        max-width: 80%;
    }
}
